{
  "channel": "#practice-notifications",
  "username": "Practice Bot",
  "text": "üÜï New Booking Alert",
  "attachments": [
    {
      "color": "good",
      "fields": [
        {
          "title": "Patient",
          "value": "{{$node['Semble Trigger - Booking Changes'].json['patient']['firstName']}} {{$node['Semble Trigger - Booking Changes'].json['patient']['lastName']}}",
          "short": true
        },
        {
          "title": "Date & Time",
          "value": "{{DateTime.fromISO($node['Semble Trigger - Booking Changes'].json['bookingDate']).toFormat('cccc, LLL dd, yyyy')}} at {{$node['Semble Trigger - Booking Changes'].json['bookingTime']}}",
          "short": true
        },
        {
          "title": "Practitioner",
          "value": "{{$node['Semble Trigger - Booking Changes'].json['practitioner']['firstName']}} {{$node['Semble Trigger - Booking Changes'].json['practitioner']['lastName']}}",
          "short": true
        },
        {
          "title": "Service",
          "value": "{{$node['Semble Trigger - Booking Changes'].json['service']['name']}}",
          "short": true
        }
      ],
      "footer": "Semble Practice Management",
      "ts": {{Math.floor(Date.now() / 1000)}}
    }
  ]
}

